//     width: 100%;
//     border-spacing: 0;

//     tr {
//       :last-child {
//         td {
//           border-bottom: 0;
//         }
//       }
//     }

//     th,
//     td {
//       margin: 0;
//       padding: 0.5rem;
//       border-bottom: 1px solid black;
//       border-right: 1px solid black;

//       /* The secret sauce */
//       /* Each cell should grow equally */
//       width: 1%;
//       /* But "collapsed" cells should be as small as possible */
//       &.collapse {
//         width: 0.0000000001%;
//       }

//       :last-child {
//         border-right: 0;
//       }
//     }
//   }

//   .pagination {
//     padding: 0.5rem;
//   }
// `

class View_month extends React.Component {

    data = "";
    parsed_data=[{}];

    componentDidMount() {
        const date=new Date();
        axios.post('http://localhost:8080/view_month',localStorage.getItem("ssid")+","+date.toISOString() , {headers:{'Content-Type': 'text/plain'}})
        .then((response)=> this.data=response.data);
        this.parsed_data=parse_vcal(this.data);
    }

    render() :JSX.Element {
        return   (<>
            <table>
                <tbody>
                    <tr>
                    <td>Monday</td>
                    <td>Tuesday</td>
                    <td>Wednesday</td>
                    <td>Thursday</td>
                    <td>Friday</td>
                    <td>Saturday</td>
                    <td>Sunday</td>
                    </tr>
                    {this.parsed_data.map((item, i) => (
                        <tr key={i}>
                        <td>{item.SUMMARY}</td>
                        <td>{item.DTSTART}</td>
                        <td>{item.DTEND}</td>
                        <td>{item.DESCRIPTION}</td>
                        <td>{item.MAILTO}</td>
                        <td>{item.ORGANIZER}</td>
                        <td>{item.DTSTAMP}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </>);
    }
}

export default View_month;
